import React, { useState, useEffect } from 'react'
import '../public/style/components/header.css'
import { Row, Col, Menu } from 'antd'
import { HomeOutlined, VideoCameraOutlined, ContainerOutlined }  from '@ant-design/icons'
import Router from 'next/router'
import Link from 'next/link'
import axios from 'axios'
import ApiUrl from '../config/urls'
import { getUrl } from '../public/utils/util'
const Header = () =>{
    const [navArray,setArray] = useState([])
    useEffect(()=>{
        const fetchData=async()=>{
            const result = await axios(ApiUrl.getNavList).then(res=>{
                console.log(res)
                return res.data
            })
            setArray(result)
            console.log('---------',navArray)
        }
        fetchData()
        // const result =async ()=>await getUrl(ApiUrl.getNavList)
        // // result()
        // setArray(result())
    },[])
    return(
        <div className="header">
            <Row align="top" justify="center">
                <Col xs={24} sm={24} md={15}>
                    <span className="header-logo">KIKA</span>
                    <span className="header-text">前端问之我答 - 技术博客</span>
                </Col>
                <Col xs={0} sm={0} md={9}>
                    <Menu mode="horizontal">
                        <Menu.Item key="home">
                            <HomeOutlined />
                            首页
                        </Menu.Item>
                        <Menu.Item key="video">
                            <VideoCameraOutlined />
                            {navArray[type_name]}
                        </Menu.Item>
                        <Menu.Item key="artical">
                            <ContainerOutlined />
                            {navArray[type_name]}
                        </Menu.Item>
                    </Menu>
                </Col>
            </Row>
        </div>
    )
}
export default Header
