//   let id = context.query.id
//   const promise = new Promise((resolve,reject)=>{
//     axios.post()
//   })
// }
Detail.getInitialProps = async (context) => {
  console.log("??????",context)
  const list = await postUrl(ApiUrl.getArticleDetail, {id:context.query.id});
  return list
};
