import React, { useState, useEffect } from 'react'
import '../public/style/components/header.css'
import { Row, Col, Menu } from 'antd'
import { HomeOutlined, VideoCameraOutlined, ContainerOutlined }  from '@ant-design/icons'
import Router from 'next/router'
import axios from 'axios'
import ApiUrl from '../config/urls'
// import { getUrl } from '../public/utils/util'


const Header = () =>{
    const [navArray,setArray] = useState([])
    useEffect(()=>{
        const fetchData=async()=>{
            console.log(ApiUrl.getNavList)
            const result = await axios(ApiUrl.getNavList).then(res=>{
                console.log(res)
                return res.data
            })
            setArray(result)
        }
        fetchData()
    },[])
    const handleClick = (e)=>{
        if(e.key=="0"){
            Router.push("/")
        }else{
            Router.push("/list?id="+e.key)
        }
    }
    return(
        <div className="header">
            <Row align="top" justify="center">
                <Col xs={24} sm={24} md={15}>
                    <span className="header-logo">KIKA</span>
                    <span className="header-text">前端问之我答 - 技术博客</span>
                </Col>
                <Col xs={0} sm={0} md={9}>
                    <Menu mode="horizontal" onClick={handleClick}>
                        <Menu.Item key="0">
                            <HomeOutlined />
                            首页
                        </Menu.Item>
                        <Menu.Item key="1">
                            <VideoCameraOutlined />
                            {navArray.length>0&&navArray[0].type_name}
                        </Menu.Item>
                        <Menu.Item key="2">
                            <ContainerOutlined />
                            {navArray.length>0&&navArray[1].type_name}
                        </Menu.Item>
                    </Menu>
                </Col>
            </Row>
        </div>
    )
}
export default Header
