import { Row, Col, Breadcrumb, Affix } from "antd";
import "../public/style/pages/detail.css";

import Head from "next/head";
import Header from "../components/header";
import Advert from "../components/ad";
import Author from "../components/author";
import Footer from "../components/footer";

import {
  FieldTimeOutlined,
  FolderOpenOutlined,
  FireOutlined,
} from "@ant-design/icons";
// import ReactMarkdown from "react-markdown";
import MarkdownNavbar from "markdown-navbar";
import "markdown-navbar/dist/navbar.css";

import marked from 'marked'
import hljs from 'highlight.js'
import 'highlight.js/styles/monokai-sublime.css'

import { postUrl } from "../public/utils/util";

export default function Detail(props) {
console.log(props)
  const renderer = new marked.Renderer()
  marked.setOptions({
    renderer:renderer,
    gfm:true, //启动github的markdown
    pedantic:false, // 容错渲染
    sanitize:false, //忽略html标签
    tables:true,  //是否输出表格，依赖gfm
    breaks:false,  //github换行符
    smartLists:true,  //渲染列表样式
    highlight:function(code){
      return hljs.highlightAuto(code).value
    }
  })
  let html = marked(props.article_content)

  return (
    <div className="container">
      <Head>
        <title>detail</title>
      </Head>
      <Header></Header>
      <Row className="common-main" justify="center">
        <Col className="common-left" xs={24} sm={24} md={16}>
          <div className="breadcrumb">
            <Breadcrumb>
              <Breadcrumb.Item>
                <a href="/">首页</a>
              </Breadcrumb.Item>
              <Breadcrumb.Item>
                <a href="/video">视频列表</a>
              </Breadcrumb.Item>
              <Breadcrumb.Item>XXXXX</Breadcrumb.Item>
            </Breadcrumb>
          </div>
          <div>
            <div className="detail-title">React Hooks</div>
            <div className="detail-desc center">
              <div className="list-icon">
                <span>
                  <FieldTimeOutlined />
                  2020-01-01
                </span>
                <span>
                  <FolderOpenOutlined />
                  技术文章
                </span>
                <span>
                  <FireOutlined />
                  1122人
                </span>
              </div>
            </div>
            <div className="detail-content"
            dangerouslySetInnerHTML={html}
            >
              {/* <ReactMarkdown source={markdown} escapeHtml={false} /> */}
            </div>
          </div>
        </Col>
        <Col className="common-right" xs={0} sm={0} md={7}>
          <Author />
          <Advert />
          <Affix offsetTop={5}>
            <div className="detail-nav common-box">
              <div className="nav-title">文章目录</div>
              <MarkdownNavbar
                source={html}
                className="article"
                headingTopOffset={-55}
                ordered={false}
              />
            </div>
          </Affix>
        </Col>
      </Row>
      <Footer />
    </div>
  );
}

